html:
{%- macro printMode(mode,x) %}

<h6 style="text-align:right;padding-top:0px!important;FONT-SIZE:10PX;color:black;padding-right:2px!important;font-weight:bold;">{{mode}}</h6>
<h6 style="text-align:center;font-size:18px;"><b style = "align:center">Delivery Challan</b></h6>
<p style="text-align:center;font-size:16px;">This Goods not for Sales Returnable</p>

<div id = "Body">
<div class="master">
    <div style="">
    <div class="left1">
        <!-- <div style = "float:left;">-->
        <!--     <img  src="http://ohmgroups:8000/files/Logo5cc1c5.jpg" style = "width:50px ;height:50px;padding-left:3px;padding-top:3px;">-->
        <!--</div>-->
            <!--<div style = "float:right;padding-right:17px;margin-top: 19px;">-->
            <!--     <b>{{ frappe.db.get_value('Address','Barani-Billing','address_title') }}</b>-->
            <!--</div>-->

            <!--<p style="border-bottom:1px solid black;padding:3px;height:90px;margin-top: 50px;line-height:1.2">-->
                <p style="border-bottom:1px solid black;line-height:1.2">
            {%set com_add = frappe.db.get_value("Dynamic Link", {"parenttype":"Address","link_doctype":"Company","link_name":doc.company},"parent")%} 


            <b>{{doc.company}} </b><br>
                  {%if com_add %}
            {%set add = frappe.get_doc("Address",com_add)%} 
            {{add.address_line1}}<br>
 {{add.address_line2}},<br>
 {{add.city}}<br>
GSTIN/UIN:  {{add.gstin}}<br>
State Name :  {{add.state}}<br>
        </p>
         {% endif %}
                  <p style="border-bottom:1px solid black;padding:3px;line-height: 1.2;height:100px;">
       Consignee (Ship to)<br>
            <b>{{doc.party_name}}</b><br>
     {%if doc.shipping_address_name %}
{%set ship_add = frappe.get_doc("Address",{"name":doc.shipping_address_name})%}
             {{ship_add.address_line1}}<br>
{{ship_add.address_line2}},{{ship_add.city}}<br>
GSTIN/UIN: {{ship_add.gstin}}<br>
State Name : {{ship_add.state}}<br>
Pincode : {{ship_add.pincode}}
{% else %}
 {%if doc.customer_address %}
 {%set add = frappe.get_doc("Address", {"name":doc.customer_address })%} 
            {{add.address_line1}}<br>
 {{add.address_line2}},
 {{add.city}}<br>
GSTIN/UIN:  {{add.gstin}}<br>
State Name :  {{add.state}}<br>
Pincode : {{add.pincode}}
        </p>
        {% endif %}
       {% endif %}
           <p style="border-bottom:1px solid black;line-height: 1.2;height:95px;">
            Buyer (Bill to)<br>
            <b>{{doc.party_name}}</b><br>

     {%if doc.customer_address %}
            {%set add = frappe.get_doc("Address", {"name":doc.customer_address })%} 
            {{add.address_line1}}<br>
 {{add.address_line2}},
 {{add.city}}<br>
GSTIN/UIN:  {{add.gstin}}<br>
State Name :  {{add.state}}<br>
Pincode : {{add.pincode}}
        </p>
         {% endif %}
        </div>

</div>
     <div class="right1" style="border-bottom:0px solid black;border-left:1px solid black;height:282px;">
            <table style="width:100%;">
                <tr>
                    <td>Delivery Challan No. <br><b> {{doc.name}}</b></td>
                    <td style="border-left:1px solid black;">Dated <br><b> {{doc.get_formatted("posting_date")}}</b></td>
                </tr>
                <tr>
                    {% set supplier=frappe.get_doc('Supplier',party_name) %}
                    <td>Mode/Terms of Payment<br><b>{% if supplier.payment_terms %}
                    {{supplier.payment_terms or "90days"}}
                    {% else %}
                    90 days
                {% endif %}</b>

                <td style="border-left:1px solid black;">E-way Bill<br><b>{% if doc.ewaybill %}{{ doc.ewaybill }}{% else %}
                    {{""}}
                    <br>{% endif %} </b></td>
                </tr>

                <tr>

                    <td>Designation.<br>{%if doc.customer_address %}{{add.city}}{% else %}<br>{% endif %}</td>
<td> </td>
                </tr>
                 <tr>
                    <td>Po No.<br><b>{% if doc.po_no %}{{doc.po_no}}{% else %}
                    {{""}}
                    <br>{% endif %}</b></td>
                    <td style="border-left:1px solid black;">Dated<br><b>{% if doc.get_formatted("po_date") %}{{ doc.get_formatted("po_date") }}{% else %}
                    {{""}}
                    <br>{% endif %} </b></td>

                </tr>
            <tr>
                    <td>Driver Name.<br><b>{% if doc.driver_name %}{{doc.driver_name}}{% else %}
                    {{""}}
                    <br>{% endif %}</b></td>
                    <td style="border-left:1px solid black;">Vehicle No.<br><b>{% if doc.vehicle_no %}{{ doc.vehicle_no }}{% else %}
                    {{""}}
                    <br>{% endif %} </b></td>

                </tr>
                <tr style="border-bottom:0px;height:138px;">
                    <td style="border-bottom:1px solid black;" colspan="2">Terms of Delivery<br><span style="line-height:1;"><br></span><br></td> 
                </tr>
            </table>
        </div>

    <hr>
    <table style ="width:100%">
        <tr style="text-align:center;border-bottom:1px solid black;border-top:1px solid black;">
            <td style="width:4%;border-right:1px solid black;">SI No.</td>
            <td style="width:55%;border-right:1px solid black;padding:6px!important;">DESCRIPTION OF GOODS</td>
            <td style="width:15%;padding:6px!important;border-right:1px solid black">Part Number</td>
            <td style="width:12%;padding:6px!important;border-right:1px solid black">Quantity</td>
            <td style="width:12%;padding:6px!important;">Amount</td>
        </tr>
        {% set taxcal={} %}
        {% set tax = [] %}
        {% set serial_no=[] %}
        {% for item in doc.items %}
        {% set _=serial_no.append(1) %}
            <tr>
                <td style="text-align:right;border-right:1px solid black;">{{ serial_no|length }}</td>
                <td style="text-align:left;border-right:1px solid black;">{{item.item_name}}</td>
                <td style="text-align:right;border-right:1px solid black;">{{item.item_code}}</td>
                {% set d=frappe.get_doc('Item',item.item_code) %}
                {% if d.taxes|length %}
                    {% set t=d.taxes[0].item_tax_template %}
                    {% set td=frappe.get_doc('Item Tax Template',t) %}
                    {% set tax=[] %}
                    {% for i in td.taxes %}
                        {% set _=tax.append(i.tax_rate) %}
                    {% endfor %}
                {% endif %}
                    {% set taxx = item.gst_hsn_code %}
                    {% if taxx in taxcal %}
                        {% set x=[] %}
                        {% set _=x.append(taxcal[taxx]) %}
                        {% set _= taxcal.update({taxx: x[0]+ item.amount})%}
                    {% else %}
                    {% set _=taxcal.update({taxx: item.amount}) %}
                    {% endif %}

                <!--<td style="text-align:center;border-right:1px solid black;">{{ (tax|sum)|int}}%</td>-->
                <td style="text-align:right;border-right:1px solid black;"><b>{{item.qty}} {{item.uom}}</b></td>
                <td style="text-align:right;"><b>{{item.amount}}</b></td>
            </tr>
        {% endfor %}
        {% for empty in range(35- doc.items|length) %}
                <tr style="height:10px;">
                    <td style="border-right:1px solid black;"></td>
                    <td style="border-right:1px solid black;"></td>
                    <td style="border-right:1px solid black;"></td>
                     <td style="border-right:1px solid black;"></td>

                </tr>
        {% endfor %}
         {%set c = [] %}
        {% set s = [] %}

      <tr style="border-top:1px solid black;border-bottom:1px solid black;text-align:right">
            <td></td>
            <td>Total</td>
            <td></td>
            <td>Qty {{doc.total_qty}} </td>
            <td>₹ {{doc.total_amount}}</td>



        </tr>
    </table>
    <table>
        <tr>
            <td style="text-align:left;border-bottom:1px solid black;">Approximate Value :₹ {{doc.approx_value or ""}}<br></td>
            <td style="text-align:right;border-bottom:1px solid black;">E. & O.E</td>
        </tr>
    </table>
    <table class="hsn">
        <!--<tfoot>-->
        <tr>
            <td>
                Customer's Seal and Signature
            </td>
            <td style="text-align:right;border-left:1px solid black;width:49.2%;">
                for {{doc.company}}
                <br>
                <br>
                <br>
                Authorised Signature
            </td>
        </tr>
        <!--</tfoot>-->
    </table>

</div>
    <div class="page-break"></div>
{%- endmacro %}   

{%- set mode = 'EXTRA COPY' %}
{{- printMode(mode,-3) }}
