html:
{%- macro printMode(mode,x) %}

<h6 style="text-align:right;padding-top:0px!important;FONT-SIZE:10PX;color:blue;padding-right:2px!important;font-weight:bold;">{{mode}}</h6>
<h6 style="text-align:center;font-size:18px;"><b>Delivery Challan</b></h6>
<p style="text-align:center;font-size:16px;">This Goods not for Sales Returnable</p>

<div id = "Body">
<div class="master">
    <div style="">
    <div class="left1">
        <!-- <div style = "float:left;">-->
        <!--     <img  src="http://ohmgroups:8000/files/Logo5cc1c5.jpg" style = "width:50px ;height:50px;padding-left:3px;padding-top:3px;">-->
        <!--</div>-->
            <!--<div style = "float:right;padding-right:17px;margin-top: 19px;">-->
            <!--     <b>{{ frappe.db.get_value('Address','Barani-Billing','address_title') }}</b>-->
            <!--</div>-->

            <!--<p style="border-bottom:1px solid black;padding:3px;height:90px;margin-top: 50px;line-height:1.2">-->
                <p style="border-bottom:1px solid black;line-height:1.2">
            {% set subcontracting_order = frappe.get_doc("Subcontracting Order",doc.subcontracting_order)%}
            {%set com_add = frappe.db.get_value("Dynamic Link", {"parenttype":"Address","link_doctype":"Company","link_name":doc.company},"parent")%} 

            <b>{{doc.company}} (from {{doc.posting_date}})</b><br>
                  {%if com_add %}
            {%set add = frappe.get_doc("Address",com_add)%} 
            {{add.address_line1}}<br>
 {{add.address_line2}},<br>
 {{add.city}}<br>
GSTIN/UIN:  {{add.gstin}}<br>
State Name :  {{add.state}}<br>
        </p>
         {% endif %}
                  <p style="border-bottom:1px solid black;padding:3px;line-height: 1.2;height:100px;">
       Consignee (Ship to)<br>
            <b>{{doc.company}}</b><br>
     {%if subcontracting_order.shipping_address %}
{%set ship_add = frappe.get_doc("Address",{"name":subcontracting_order.shipping_address})%}
             {{ship_add.address_line1}}<br>
{{ship_add.address_line2}},{{ship_add.city}}<br>
GSTIN/UIN: {{ship_add.gstin}}<br>
State Name : {{ship_add.state}}<br>
Pincode : {{ship_add.pincode}}
        </p>
       {% endif %}
           <p style="border-bottom:1px solid black;line-height: 1.2;height:95px;">
            Buyer (Bill to)<br>
            <b>{{subcontracting_order.supplier}}</b><br>

     {%if subcontracting_order.supplier_address %}
{%set dis_add = frappe.get_doc("Address",{"name":subcontracting_order.supplier_address})%}

            {{dis_add.address_line1 or ""}},<br>
            {{dis_add.address_line2 or  ""}}, {{dis_add.city or  ""}}<br>
             GSTIN/UIN: {{dis_add.gstin or ""}}<br>
             State Name : {{dis_add.state or  ""}}<br>
             Pincode : {{dis_add.pincode}}
        </p>
        {% endif %}
        </div>
        <div class="right1" style="border-bottom:0px solid black;border-left:1px solid black;height:282px;">
            <table style="width:100%;">
                <tr>
                    <td>Delivery Note No. <br><b> {{doc.name}}</b></td>
                    <td style="border-left:1px solid black;">Dated <br><b> {{doc.get_formatted("posting_date")}}</b></td>
                </tr>
                <tr>
                    {% set supplier=frappe.get_doc('Supplier',subcontracting_order.supplier) %}
                    <td>Mode/Terms of Payment<br><b>{% if supplier.payment_terms %}
                    {{supplier.payment_terms or "90days"}}
                    {% else %}
                    90 days
                {% endif %}</b>

                    <td><br></td>
                </tr>
                    {%set po_date = frappe.get_doc("Purchase Order",subcontracting_order.purchase_order)%}

                <tr>

                    <td>Designation.<br>{%if subcontracting_order.supplier_address %}{{dis_add.city}}{% else %}<br>{% endif %}</td>
<td> </td>
                </tr>
                 <tr>
                    <td>Po No.<br><b>{% if po_date.name %}{{po_date.name}}{% else %}
                    {{""}}
                    <br>{% endif %}</b></td>
                    <td style="border-left:1px solid black;">Dated<br><b>{% if po_date.get_formatted("transaction_date") %}{{ po_date.get_formatted("transaction_date") }}{% else %}
                    {{""}}
                    <br>{% endif %} </b></td>

                </tr>
            <tr>
                    <td>Driver Name.<br><b>{% if po_date.driver_name %}{{po_date.driver_name}}{% else %}
                    {{""}}
                    <br>{% endif %}</b></td>
                    <td style="border-left:1px solid black;">Vehicle No.<br><b>{% if po_date.get_formatted("vehicle_no") %}{{ po_date.get_formatted("vehicle_no") }}{% else %}
                    {{""}}
                    <br>{% endif %} </b></td>

                </tr>
                <tr style="border-bottom:0px;height:139px;">
                    <td style="border-bottom:1px solid black;" colspan="2">Terms of Delivery<br><span style="line-height:1;">{% if po_date.terms %}{{ po_date.terms }}{% else %}<br>{% endif %}</span><br></td> 
                </tr>
            </table>
        </div>
    </div>
    <hr>

 <table style ="width:100%">
        <tr style="text-align:center;border-bottom:1px solid black;border-top:1px solid black;">
            <td style="width:4%;border-right:1px solid black;">SI No.</td>
            <td style="width:55%;border-right:1px solid black;padding:6px!important;">DESCRIPTION OF GOODS</td>
            <td style="width:20%;padding:6px!important;border-right:1px solid black">Part Number</td>
            <td style="width:10%;padding:6px!important;border-right:1px solid black">Quantity</td>
        </tr>
        {% set taxcal={} %}
        {% set tax = [] %}
        {% set serial_no=[] %}
        {% set c = [] %}
        {% for item in doc.items %}
        {% set _=serial_no.append(1) %}

            <tr>
                <td style="text-align:center;border-right:1px solid black;">{{ serial_no|length }}</td>
                <td style="border-right:1px solid black;">{{item.item_name}}</td>
                <td style="border-right:1px solid black;">{{item.item_code}}</td>
                {% set d=frappe.get_doc('Item',item.item_code) %}
                {% if d.taxes|length %}
                    {% set t=d.taxes[0].item_tax_template %}
                    {% set td=frappe.get_doc('Item Tax Template',t) %}
                    {% set tax=[] %}
                    {% for i in td.taxes %}
                        {% set _=tax.append(i.tax_rate) %}
                    {% endfor %}
                {% endif %}
                    {% set taxx = item.gst_hsn_code %}
                    {% if taxx in taxcal %}
                        {% set x=[] %}
                        {% set _=x.append(taxcal[taxx]) %}
                        {% set _= taxcal.update({taxx: x[0]+ item.amount})%}
                    {% else %}
                    {% set _=taxcal.update({taxx: item.amount}) %}
                    {% endif %}

                <!--<td style="text-align:center;border-right:1px solid black;">{{ (tax|sum)|int}}%</td>-->
                <td style="text-align:center;border-right:1px solid black"><b>{{item.qty}} {{item.uom}}</b></td>
            </tr>
            {% set _ = c.append(item.qty)%}
        {% endfor %}
        {% for empty in range(35- doc.items|length) %}
                <tr style="height:10px;">
                    <td style="border-right:1px solid black;"></td>
                    <td style="border-right:1px solid black;"></td>
                    <td style="border-right:1px solid black;"></td>

                </tr>
        {% endfor %}

      <tr style="border-top:1px solid black;border-bottom:1px solid black;">
            <td></td><td></td>
            <td>Total</td>
            <td >{{c|sum}}</td>

        </tr>

    </table>
    <table>
        <tr>
            <td style="text-align:right;border-bottom:1px solid black;"> <br><br></td>
            <td style="text-align:right;border-bottom:1px solid black;">E. & O.E</td>
        </tr>
    </table>
    <table class="hsn">
        <!--<tfoot>-->
        <tr>
            <td>
                Customer's Seal and Signature
            </td>
            <td style="text-align:right;border-left:1px solid black;width:49.2%;">
                for {{doc.company}}
                <br>
                <br>
                <br>
                Authorised Signature
            </td>
        </tr>
        <!--</tfoot>-->
    </table>

</div>
 <div class="page-break"></div>
 {%for j in subcontracting_order.items %}
 {%set bom = frappe.get_all("BOM",filters={"item":j.item_code,"is_active":1,"is_default":1,"docstatus":1},pluck="name") %}
 {% if bom%}
 <b><u>{{loop.index or ""}}. {{j.item_name}}</u> - {{doc.name}} -<b>{% if doc.get_formatted("posting_date") %}{{ doc.get_formatted("posting_date") }}{% else %}<br>{% endif %} </b></b>
  {%endif%}

 <br>

  <table style = "border:1px solid black;top:2px;margin-top:50!important;text-align:center;font-size:11px;">
 {% if bom%}
     <tr  style="border-bottom:1px solid black;border-top:1px solid black;width:100;">


         <td style="width:40%;border-right:1px solid black;vertical-align:middle!important"><b>{{loop.index or ""}} {{j.item_name}}</b></td>
         <td style="width:5%;border-right:1px solid black;vertical-align:middle!important"><b> Part Number</b></td>
            <td style="width:5%;border-right:1px solid black;vertical-align:middle!important"><b> Finished Goods Qty</b></td>
            <td style="width:20%;border-right:1px solid black;vertical-align:middle!important"><b> Image</b></td>


             <td style="width:10%;border-right:1px solid black;vertical-align:middle!important"><b> Checked by {{doc.company}}</b></td>

            <td style="width:10%;border-right:1px solid black;vertical-align:middle!important"> <b>Checked by Supplier</b></td>
        </tr>
        {%endif%}
          {%for k in bom%}
      {%set bom_item = frappe.get_doc("BOM",k)%}
         {% for child in bom_item.items %}
         {% if child %}
        <tr  style="border-bottom:1px solid black;border-top:1px solid black;width:100">
            <td style="padding-left:2;vertical-align:middle!important">{{child.item_name}}</td>
            <td style="padding-left:2;border-left:1px solid black;border-right:1px solid black;text-align: center;vertical-align:middle!important">{{child.item_code}}</td>
             <td style="padding-left:2;border-left:1px solid black;border-right:1px solid black;text-align: center;vertical-align:middle!important">{{j.qty}} {{j.stock_uom}}</td>
            {%if child.image %}
            <td style="padding-left:2;border-left:1px solid black;border-right:1px solid black;text-align: center;vertical-align:middle!important"><img src="{{child.image}}" width="100" height="100"></td>

            {%endif%}
                         <td style="padding-left:2;border-left:1px solid black;border-right:1px solid black;text-align: center;vertical-align:middle!important"><img src="{{child.image}}" width="100" height="100"></td>

            <td style="padding-left:2;border-left:1px solid black;border-right:1px solid black;text-align: center;vertical-align:middle!important"><input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"></td>
            <td style="padding-left:2;border-left:1px solid black;border-right:1px solid black;text-align: center;vertical-align:middle!important"><input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"></td>
        </tr>
      {% endif%}   
      {% endfor %}

     {% endfor %}

 </table>


  {% endfor %}


</div>
    <div class="page-break"></div>

{%- endmacro %}   

{%- set mode = 'ORIGINAL' %}
{{- printMode(mode,-3) }}
{%- set mode = 'DUPLICATE' %}
{{- printMode(mode,0) }}
{%- set mode = 'TRIPLICATE' %}
{{- printMode(mode,-3) }}
{%- set mode = 'EXTRA COPY' %}
{{- printMode(mode,0) }}
