css:
hr { 
  margin-top: 0em !important;
  margin-bottom: 0em !important;
  margin-left: auto;
  margin-right: auto;
  border-style: inset;
  border-width: 1px;
  background-color:black !important;
  color:black !important
}

html:
 {% for i in doc.items %}

<style>
	.print-format table, .print-format tr, 
	.print-format td, .print-format div, .print-format p {
		font-family: Monospace;
		line-height: 110%;
		vertical-align: middle;
		font-size:9;
	}
	@media screen {
		.print-format {
			width: 65mm;
			padding: 6.35mm;
			min-height: 2mm;
		}
	}
	@media print {
        .pagebreak { 
            page-break-before: always; 
        }
    }
</style>

{% set company = frappe.db.get_single_value("Global Defaults","default_company") %}

<div>
    <p align="center"><span style="font-size:14px;font-family: Geneva, Verdana, sans-serif;">{{company}}</span></p>
        <hr>

    <div>
        <table style="width:100%">
            <tr >
            <td style = "font:right;width:70%">

            Part No&nbsp;&nbsp;:&nbsp;{% if i.item_code %}{{i.item_code}}<br>{% else %}{{""}}{% endif %}
            Qty&ensp;&ensp;&emsp;&emsp;&emsp;&emsp;:&nbsp;{{i.qty}} <br>
            {{doc.customer}}    <br>
             {% set qc = frappe.get_doc("Quality Inspection",{"reference_name":doc.name,"docstatus":1}) %}
             {% if qc.status == "Accepted" %}
             {{"Checked Ok"}}{% else %}
             {{"Rejected"}}{% endif %}

        </td> 

        <td style = "font:left;width:30%">

        <img src="https://barcode.tec-it.com/barcode.ashx?data=
             {% set qc = frappe.get_doc("Quality Inspection",{"reference_name":doc.name,"item_code":i.item_code}) %}
        {{qc.name}}&code=QRCode"  width="40px" heigth="40px">
        </td>  
            </tr>



        </table>

        <!--<div>-->
        <!--    <div style="float:right;">-->
        <!--        <img src="https://barcode.tec-it.com/barcode.ashx?data=123&code=QRCode" width="40px" heigth="40px">-->
        <!--    </div>-->
        <!--</div>-->
    </div>
</div>
   <div class="pagebreak"></div>

{% endfor %}
